var Clock={element:$("#clock"),timer:null,add:function(){if(Clock.element.length==0){Clock.element=$('<div id="clock" class="color"><span id="hour">00</span><img src="templates/default/img/colon.gif"><span id="minutes">00</span></div>');$("body").prepend(Clock.element)}if(Clock.timer==null){Clock.timer=window.setInterval(Clock.refresh,500)}Clock.element.show()},remove:function(){Clock.element.hide();window.clearInterval(Clock.timer);Clock.timer=null},refresh:function(){var b=new Date();var a=b.getHours();var c=b.getMinutes();a=(a<10?"0":"")+a;c=(c<10?"0":"")+c;Clock.element.find("#hour").html(a);Clock.element.find("#minutes").html(c)},destroy:function(){Clock.element.empty();Clock.element.remove();window.clearInterval(Clock.timer);Clock.timer=null}};Clock.add();var Overlay={element:$("#overlay"),add:function(a){if(Overlay.element.length==0){Overlay.element=$('<div id="overlay"></div>');$("body").prepend(Overlay.element)}Overlay.element.css("background-image","url("+a+")");Overlay.element.show()},remove:function(){Overlay.element.hide()},destroy:function(){Overlay.element.remove()}};